{
    Origin: http://baidu.com
    withCredentials: true
}
// response-header
{
     Access-Control-Allow-Origin: http://baidu.com  // 不能使用通配符
     Access-Control-Allow-Credentials: true  //允许发送Cookie和http认证信息
}

